import React, { useEffect } from "react";

export default function Home() {
  useEffect(() => {
    // Ported page scripts: filters, search, ordering and add-to-cart
    const checkboxes = Array.from(document.querySelectorAll('.filtros input[type=checkbox]'));
    const productos = Array.from(document.querySelectorAll('.producto'));
    const ordenar = document.getElementById('ordenar');
    const buscador = document.getElementById('buscador');

    function filtrarProductos() {
      const seleccionadas = checkboxes.filter(c => c.checked).map(c => c.value);
      const texto = (buscador && buscador.value ? buscador.value : '').toLowerCase();

      productos.forEach(prod => {
        const categoria = prod.dataset.categoria;
        const nombre = (prod.dataset.nombre || '').toLowerCase();
        const coincideCategoria = seleccionadas.length === 0 || seleccionadas.includes(categoria);
        const coincideTexto = nombre.includes(texto);
        prod.style.display = coincideCategoria && coincideTexto ? 'block' : 'none';
      });
    }

    checkboxes.forEach(cb => cb.addEventListener('change', filtrarProductos));
    if (buscador) buscador.addEventListener('input', filtrarProductos);

    const ordenarHandler = () => {
      const contenedor = document.querySelector('.productos');
      if (!contenedor) return;
      const items = Array.from(contenedor.children);
      items.sort((a, b) => {
        switch (ordenar.value) {
          case 'precio-asc': return parseInt(a.dataset.precio) - parseInt(b.dataset.precio);
          case 'precio-desc': return parseInt(b.dataset.precio) - parseInt(a.dataset.precio);
          case 'nombre': return a.dataset.nombre.localeCompare(b.dataset.nombre);
          default: return 0;
        }
      });
      items.forEach(i => contenedor.appendChild(i));
    };
    if (ordenar) ordenar.addEventListener('change', ordenarHandler);

    const addButtons = Array.from(document.querySelectorAll('.add-to-cart'));
    const handlers = [];
    addButtons.forEach(btn => {
      const h = function() {
        const producto = this.closest('.producto');
        this.textContent = '¬°Agregado!';
        this.style.background = '#1E90FF';
        this.style.color = 'white';
        setTimeout(() => {
          this.textContent = 'Agregar al Carrito';
          this.style.background = '#39FF14';
          this.style.color = '#000';
        }, 2000);
      };
      btn.addEventListener('click', h);
      handlers.push({ btn, h });
    });

    filtrarProductos();

    return () => {
      checkboxes.forEach(cb => cb.removeEventListener('change', filtrarProductos));
      if (buscador) buscador.removeEventListener('input', filtrarProductos);
      if (ordenar) ordenar.removeEventListener('change', ordenarHandler);
      handlers.forEach(({btn,h}) => btn.removeEventListener('click', h));
    };
  }, []);

  return (
    <div>
      <header>
        <div className="header-content">
          <div className="logo">LEVEL UP</div>
          <div className="nav-buttons">
            <a href="/Inicio-sesion/inicio-sesion.html" className="btn btn-primary">Iniciar Sesi√≥n</a>
            <a href="/registro/registro.html" className="btn btn-secondary">Registrarse</a>
          </div>
        </div>
      </header>

      <div className="main-container">
        <section className="hero-section">
          <h1 className="hero-title">¬°Sube de Nivel!</h1>
          <p className="hero-subtitle">Descubre los mejores productos gaming y lleva tu experiencia al siguiente nivel</p>
        </section>

        <div className="catalogo">
          <aside className="filtros">
            <h3>üéÆ Categor√≠as</h3>
            <ul>
              <li><input type="checkbox" id="juegos" value="juegos" /><label htmlFor="juegos">Juegos de mesa</label></li>
              <li><input type="checkbox" id="accesorios" value="accesorios" /><label htmlFor="accesorios">Accesorios</label></li>
              <li><input type="checkbox" id="consolas" value="consolas" /><label htmlFor="consolas">Consolas</label></li>
            </ul>

            <h3>üìä Ordenar por</h3>
            <select id="ordenar">
              <option value="default">M√°s Populares</option>
              <option value="precio-asc">Precio: Menor a Mayor</option>
              <option value="precio-desc">Precio: Mayor a Menor</option>
              <option value="nombre">Nombre A-Z</option>
            </select>

            <h3>üîç Buscar</h3>
            <input type="text" id="buscador" placeholder="Buscar producto..." />
          </aside>

          <section className="productos">
            <div className="producto" data-categoria="consolas" data-precio="450000" data-nombre="PlayStation 5">
              <img src="data:image/gif;base64,R0lGODlhAQABAIAAAAUEBA==" alt="PlayStation 5" />
              <div className="categoria">Consolas</div>
              <h4>PlayStation 5</h4>
              <div className="precio">$450.000</div>
              <button className="add-to-cart">Agregar al Carrito</button>
            </div>

            <div className="producto" data-categoria="mouse" data-precio="85000" data-nombre="Logitech G Pro X">
              <img src="data:image/gif;base64,R0lGODlhAQABAIAAAAUEBA==" alt="Mouse Gaming" />
              <div className="categoria">Mouse Gaming</div>
              <h4>Logitech G Pro X</h4>
              <div className="precio">$85.000</div>
              <button className="add-to-cart">Agregar al Carrito</button>
            </div>
          </section>
        </div>
      </div>

      <iframe
        src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d1614.3218665764903!2d-70.6846115622053!3d-33.5988123187725!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x9662d97348990efd%3A0xce94f054c9e23bbf!2sCarlos%20Condell%2C%20San%20Bernardo%2C%20Regi%C3%B3n%20Metropolitana!5e0!3m2!1ses-419!2scl!4v1757420353991!5m2!1ses-419!2scl"
        width="100%"
        height="450"
        style={{ border: 0 }}
        allowFullScreen
        loading="lazy"
        referrerPolicy="no-referrer-when-downgrade"
      />

      <a href="https://wa.me/56945002960" className="whatsapp-float" target="_blank" rel="noreferrer" aria-label="Chat en WhatsApp">
        <i className="fa-brands fa-whatsapp whatsapp-icon" />
      </a>
    </div>
  );
}
