import React, { useState } from "react";

export default function Registro() {
  const [mensaje, setMensaje] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    setMensaje('Registro simulado (migrado)');
  };

  return (
    <div className="container py-5">
      <h1 className="text-center mb-4">Registro LevelUp</h1>
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card p-4">
            <h3 className="text-center mb-3">Crea tu cuenta</h3>
            <form id="formRegistro" onSubmit={handleSubmit}>
              <input type="text" id="nombre" className="form-control" placeholder="Nombre completo" required />
              <input type="email" id="correo" className="form-control" placeholder="Correo electrónico" required />
              <input type="password" id="password" className="form-control" placeholder="Contraseña" required />
              <button type="submit" className="btn btn-primary w-100">Registrarse</button>
            </form>
            <a href="/login" className="link-login">O iniciar sesión</a>
            {mensaje && <div id="mensaje" className="alert" role="alert" style={{marginTop:12}}>{mensaje}</div>}
          </div>
        </div>
      </div>
    </div>
  );
}
